const predefinedButtons = document.querySelectorAll('.predefined-button');
const donationAmountInput = document.getElementById('donationAmount');
const mainButton = document.getElementById('donateButton');

// Function to set predefined donation amounts and handle button states
function setAmount(amount) {
  donationAmountInput.value = amount;
  predefinedButtons.forEach((button) => { button.disabled = true; });
  mainButton.classList.add('loading');
  mainButton.disabled = true;
  mainButton.innerHTML = 'Donating...';
}

// Function to connect to TronLink wallet and send donation
async function sendDonation(amount) {
  const recipient = 'TSJYQL5vd1kGQu3Aedtkfug2UzCdAqN5mt'; // Replace with actual recipient address
  const amountInSun = tronWeb.toSun(amount);
  try {
    const transaction = await tronWeb.transactionBuilder.sendTrx(recipient, amountInSun);
    const signedTransaction = await tronWeb.trx.sign(transaction);
    const result = await tronWeb.trx.sendRawTransaction(signedTransaction);
    if (result.result) {
      console.log('Transaction successful:', result.txid);
      showSuccess();
    } else {
      throw new Error('Transaction failed');
    }
  } catch (error) {
    console.error('Donation error:', error);
    alert('Donation failed, please try again.');
  }
}

// Function to display success message
function showSuccess() {
  predefinedButtons.forEach((button) => {
    button.disabled = false;
    button.classList.remove('loading');
  });
  mainButton.classList.remove('loading');
  mainButton.classList.add('success');
  mainButton.innerHTML = '<span class="checkmark">âœ“</span> Donation Successful';
  setTimeout(() => {
    mainButton.classList.remove('success');
    mainButton.disabled = false;
    mainButton.innerHTML = 'Donate TRX';
  }, 3000);
}

// Add event listeners for donation actions
mainButton.addEventListener('click', async () => {
  const amount = donationAmountInput.value;
  if (isNaN(amount) || amount <= 0) {
    alert('Please enter a valid donation amount.');
    return;
  }
  await sendDonation(amount);
});

predefinedButtons.forEach((button) => {
  button.addEventListener('click', () => {
    setAmount(button.value);
    const amount = document.getElementById('donationAmount').value;
    sendDonation(amount);
  });
});

