document.addEventListener("DOMContentLoaded", function () { 
    let selectedTokenAddress; 
    let amount = 0; 
    const amountInput = document.getElementById("donationAmount"); 
    const donateButton = document.getElementById("donateButton"); 
    const recipientAddress = document.getElementById("recipientAddress"); 
    const tokenSelect = document.getElementById("token"); 

    tokenSelect.addEventListener("change", function () { 
      selectedTokenAddress = this.value; 
      console.log("Selected Token Address:", selectedTokenAddress); 
    }); 

    amountInput.addEventListener("input", function (event) { 
      amount = Number(event.target.value); 
    }); 

    async function sendTRC20Donation() {
      const recipient = recipientAddress.value;
      if (!selectedTokenAddress || amount <= 0 || !recipient) {
        alert('Please fill in all details correctly.');
        return;
      }
      try {
        const contract = await tronWeb.contract().at(selectedTokenAddress);
        const result = await contract.transfer(recipient, tronWeb.toSun(amount)).send();
        console.log('Transaction successful:', result);
        alert('Donation successful!');
      } catch (error) {
        console.error('Error with TRC-20 donation:', error);
        alert('Donation failed, please try again.');
      }
    }

    donateButton.addEventListener("click", sendTRC20Donation); 
});
